import { connect } from 'react-redux';

function DefaultOptions () {
    const headers = {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': this.props.token.accessToken + ' ' + this.props.token.type
    }
    console.log(headers);
    
}

const mapStateToProps = ({token}) => {
    return {
        token
    }
};

export default connect(mapStateToProps, undefined)(DefaultOptions);
