const defaultOptions = {
        
    headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': localStorage.getItem('token.type') + ' ' + localStorage.getItem('token.accessToken')
    },
  };
  
  export default defaultOptions;
