import { REGISTRATION } from '../action-types/';

const initialState = {
    name: '',
    surname: '',
    email: '',
    password: ''
};

export default (state = initialState, action) => {
    switch(action.type){
        case REGISTRATION: 
            const { name, value } = this.initialState;
            this.setState({
                [name]: value
            });
            return {state}

        default: return state;

    }
}
