import React, { Component } from 'react';
import  './layout.css'

class Layout extends Component {
       
    render() {
        let loggedIn = false;
        if(localStorage.getItem('accessToken')){
            loggedIn = true;
        }
            
        return (
            <div id="main">
                <header>
                    <div className='home'>
                        <a href="/" >Home</a>
                    </div>
                    <div className='sine'>
                        <a href="/register" >Register</a>
                        {loggedIn ? <a href="/logout">Logout</a> : <a href="/login" >Login</a>}    
                    </div>   
                </header>
                
                {this.props.children}
            </div>
        )
    }

}

export default Layout;
