import { REGISTRATION } from '../action-types/';
import defaultOptionenWithOutAuth from '../helpers/defaultOptionenWithOutAuth';

const initialState = {
    name: '',
    surname: '',
    email: '',
    password: ''
};

export default (state = initialState, action) => {
    switch(action.type){
        case REGISTRATION: 
       
            
        fetch('http://localhost:3300/user', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'POST',
            body: JSON.stringify({ name: this.initialState.name, surname: this.initialState.surname, email: this.initialState.email, password: this.initialState.password })
        })
        .then(response => response.json())   
            return this.initialState

        default: return state;

    }
}
