import React, { Component } from 'react';
import defaultOptionenWithOutAuth from '../../helpers/defaultOptionenWithOutAuth';
import { connect } from 'react-redux';

class CreateUser extends Component {

    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         name: '',
    //         surname: '',
    //         email: '',
    //         password: ''
    //     };

    //     this.handleChange = this.handleChange.bind(this);
    //     this.handleSubmit = this.handleSubmit.bind(this);
    // }

    handleChange(event) {
        const { name, value } = event.target;
        let state = this.props;
        state.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        fetch('http://localhost:3300/user', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'POST',
            body: JSON.stringify({ name: this.state.name, surname: this.state.surname, email: this.state.email, password: this.state.password })
        })
            .then(response => response.json())
            .then(data => {
                this.props.history.push("/login");
            });
    }
    
    render() {
        return (
            <div>
                <h1>Register</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>Name:
                <input type="text" name="name" value={this.props.name} onChange={this.handleChange} />
                    </label>
                    <label>Surname:
                <input type="text" name="surname" value={this.props.surname} onChange={this.handleChange} />
                    </label>
                    <label>Email:
                <input type="email" name="email" value={this.props.email} onChange={this.handleChange} />
                    </label>
                    <label>Password:
                <input type="password" name="password" value={this.props.password} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div >
        )
    }
}
const mapStatetoProps = ({name, surname, email, password}) => {
    return {
        name,
        surname,
        email,
        password
    }
};

export default connect(mapStatetoProps)(CreateUser);
