import React, { Component } from 'react';
import { connect } from 'react-redux';
import { authorization, register } from '../../../actions/register-actions'

class CreateUser extends Component {

    constructor(props) {
        super(props);
       
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        fetch('http://localhost:3300/user', {
            headers: { 
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': this.props.token.type + ' ' + this.props.token.accessToken
            },
            method: 'POST',
            body: JSON.stringify({ name: this.state.name, surname: this.state.surname, email: this.state.email, password: this.state.password })
        })
            .then(response => response.json())
            .then(data => {
                this.props.authorization(data);
                this.props.history.push("/profile");
            });
    }
    
    render() {
        return (
            <div>
                <h1>Register</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>Name:
                <input type="text" name="name" onChange={this.handleChange} />
                    </label>
                    <label>Surname:
                <input type="text" name="surname" onChange={this.handleChange} />
                    </label>
                    <label>Email:
                <input type="email" name="email"  onChange={this.handleChange} />
                    </label>
                    <label>Password:
                <input type="password" name="password"  onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div >
        )
    }
}
const mapStateToProps = ({email, password, token}) => {
    return {
        email,
        password,
        token
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        register: (payload) => dispatch(register(payload)),
        authorization: (payload) => dispatch(authorization(payload))
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(CreateUser);
