import { REGISTRATION } from '../action-types/';

const initialState = {
    name: '',
    surname: '',
    email: '',
    password: ''
};

export default (state = initialState, action) => {
    switch(action.type){
        case REGISTRATION:     
            handleChange = (event) => {
                const { name, value } = event.target;
                this.setState({
                    [name]: value
                });
            }
            return {state};

        default: return state;

    }
}
