    //         method: 'POST',
    //         body: JSON.stringify({ name: this.state.name, surname: this.state.surname, email: this.state.email, password: this.state.password })
    //     })
    //         .then(response => response.json())
    //         .then(data => {
    //             this.props.history.push("/login");
    //         });
    // }
    handleSubmit(state) {
        event.preventDefault();

        fetch('http://localhost:3300/user', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'POST',
            body: JSON.stringify({ name: state.name, surname: state.surname, email: state.email, password: state.password })
        })
            .then(response => response.json())
            .then(data => {
                this.props.history.push("/login");
            });
    }

    render() {
        return (
            <div>
                <h1>Register</h1>
                <form onSubmit={this.handleSubmit}>
                    <label>Name:
                <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
                    </label>
                    <label>Surname:
                <input type="text" name="surname" value={this.state.surname} onChange={this.handleChange} />
                    </label>
                    <label>Email:
                <input type="email" name="email" value={this.state.email} onChange={this.handleChange} />
                    </label>
                    <label>Password:
                <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div >
        )
    }
}

export default connect(handleSubmit)(CreateUser);
