import { connect } from 'react-redux';
import React from 'react';

export class DefaultOptions extends React{
    constructor(props) {
        super(props);     
    }
    headers = {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': this.props.token.accessToken + ' ' + this.props.token.type
    }
}

const mapStateToProps = ({token}) => {
    return {
        token
    }
};

export default connect(mapStateToProps, undefined)(DefaultOptions);
