import React, { Component } from 'react';
import defaultOptionenWithOutAuth from '../../helpers/requestHeader/defaultOptionenWithOutAuth';
import { connect } from 'react-redux';
import { sendCodeAndEmail } from '../../actions/register-actions';

class UpdataPassword extends Component {
    constructor(props) {
        super(props);
        
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        fetch('http://localhost:3300/login/user/forgotchangepassword', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'PUT',
            body: JSON.stringify({ email: this.state.email, password: this.state.password, passwordVerify: this.state.verifyPassword, forgotecodes: this.state.codes })
        })
            .then(response => { response.json() })
            .then(data => {
                this.props.history.push("/login");
            })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Email:
                        <input type="email" name="email" value={this.state.email} onChange={this.handleChange} />
                    </label>
                    <label>Password:
                        <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    </label>
                    <label>Verify password:
                        <input type="password" name="verifyPassword" value={this.state.verifyPassword} onChange={this.handleChange} />
                    </label>
                    <label>Enter your codes:
                        <input type="text" name="codes" value={this.state.codes} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}
 
export default UpdataPassword;
