import { connect } from 'react-redux';
import React, { Component } from 'react';

export class DefaultOptions extends React.Component {
    constructor(props) {
        super(props);
        const headers = {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
            'Authorization': this.props.token.accessToken + ' ' + this.props.token.type
        }
        console.log(this.props.token.accessToken);       
    }
}

const mapStateToProps = ({token}) => {
    return {
        token
    }
};

export default connect(mapStateToProps, undefined)(DefaultOptions);
