import React, { Component } from 'react';
import defaultOptionenWithOutAuth from '../../helpers/defaultOptionenWithOutAuth';

class ForgoteChangePassword extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            email: '',
            forgotecodes: ''
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;

        this.setState({
            [name]: value
        });
    }

    handleSubmit(event) {
        event.preventDefault();

        const {email, forgotecodes } = this.state;

        fetch('http://localhost:3300/login/user/verifyemailforgotecodes', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'PUT',
            body: JSON.stringify({ email, forgotecodes })
        })
            .then(response => { 
                console.log(response);
                
                response.json() })
            .then(data => {
                //this.props.history.push("/setpasswordandupdate");
            })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <label>Email:
                        <input type="email" name="email" value={this.state.email} onChange={this.handleChange} />
                    </label>
                    <label>Enter your codes:
                        <input type="text" name="forgotecodes" value={this.state.forgotecodes} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}
 
export default ForgoteChangePassword;
