import React, { Component } from 'react';
import { registration } from '../../../button';
class CreateUser extends Component {

    constructor(props) {
        super(props);

        this.state = {
            name: '',
            surname: '',
            email: '',
            password: ''
        };


        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    // handleSubmit(event) {
    //     event.preventDefault();

    //     fetch('http://localhost:3300/user', {
    //         headers: defaultOptionenWithOutAuth.headers,
    //         method: 'POST',
    //         body: JSON.stringify({ name: this.state.name, surname: this.state.surname, email: this.state.email, password: this.state.password })
    //     })
    //         .then(response => response.json())
    //         .then(data => {
    //             this.props.history.push("/login");
    //         });
    // }
    
    render() {
        return (
            <div>
                <h1>Register</h1>
                <form onSubmit={registration.bind(this)}>
                    <label>Name:
                <input type="text" name="name" value={this.name} onChange={this.handleChange} />
                    </label>
                    <label>Surname:
                <input type="text" name="surname" value={this.surname} onChange={this.handleChange} />
                    </label>
                    <label>Email:
                <input type="email" name="email" value={this.email} onChange={this.handleChange} />
                    </label>
                    <label>Password:
                <input type="password" name="password" value={this.password} onChange={this.handleChange} />
                    </label>
                    <input type="submit" value="Submit" />
                </form>
            </div >
        )
    }
}
export default CreateUser;
