import React, { Component } from 'react';
import  './layout.css'

class Layout extends Component {
    constructor(props) {
    super(props);

    this.toggle = this.toggle.bind(this);
    this.state = {
        isOpen: false,


    };
}
toggle() {
    if(localStorage)
    this.setState({
        isOpen: !this.state.isOpen,
    });
}
    render() {
        return (
            <div id="main">
                <header>
                    <div className='home'>
                        <a href="/" >Home</a>
                    </div>
                    <div className='sine'>
                        <a href="/register" >Register</a>
                        <a href="/login" if={localStorage}>{this.if ? 'Login' : 'Logout'}</a>    
                    </div>   
                </header>
                
                {this.props.children}
            </div>
        )
    }

}

export default Layout;
