import React, { Component } from 'react';
import  './layout.css'

class Layout extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLogin: false
        };
    }
    loginLogaut = () =>{
        if(localStorage){
            this.setState({isLogin: true});
        }
    }
    

    render() {
        const isLogin = this.state.isLogin; 
        let a;
        if(localStorage){
            this.setState({isLogin: true});
        }
        if(isLogin){
            a = <a>Logout</a>
        } else {
            a = <a href="/login" >Login</a>
        }
        return (
            <div id="main">
                <header>
                    <div className='home'>
                        <a href="/" >Home</a>
                    </div>
                    <div className='sine'>
                        <a href="/register" >Register</a>
                      {this.state.isLogin}    
                    </div>   
                </header>
                
                {this.props.children}
            </div>
        )
    }

}

export default Layout;
