import React, { Component } from 'react';
import { connect } from 'react-redux';
import DefaultOptionen from '../../../helpers/defaultOptions'

class Profile extends Component {

    constructor(props) {
        super(props);
              console.log(this.props.token);
              

        fetch('http://localhost:3300/user/profile', {
            headers: { 
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                'Authorization': this.props.token 
            },
            method: 'GET'
        })
        .then(response => response.json())
        .then(data => {        
            this.setState({id: data.accessUser.id, name: data.accessUser.name, surname: data.accessUser.surname});
        });

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    };

    handleSubmit(event) {
        event.preventDefault();
        fetch('http://localhost:3300/user?name=' + this.props.text, {
            headers: DefaultOptionen.headers,
            method: 'GET'
        })
        .then(response => response.json())
        .then(data => {            
            this.setState((state) => {

                let oldState = state;
                oldState.users = data.accessUser;
                return oldState;
            });            
        });

    };

    static renderUsersList(users) {
        return (
            <ul>  
                {users.map(user =>
                    <li key={user.id}> 
                        <a href={'/userprofile/' + user.id}>{user.name} {user.surname}</a>     
                    </li>
                )}
            </ul>
        );
    }

    render() {
       
        let usersContent = this.props.users > 0 ? Profile.renderUsersList(this.props.users) : <div>User not found</div>;
        return (
            <div>
               My profile
               {this.state.id}, {this.state.name}, {this.state.surname}

               <form onSubmit={this.handleSubmit}>
                    <label>Find By Name:
                <input type="text" name="text" value={this.props.text} onChange={this.handleChange} />
                    </label>
                <input type="submit" value="Submit" />
                </form>

                <div >
                    {usersContent} 
                </div>
            </div>
        )
    }

}

const mapStateToProps = ({token}) => {
    return {
        token
    }
};


export default connect(mapStateToProps, undefined)(Profile);
