import { REGISTRATION } from '../action-types/';
import defaultOptionenWithOutAuth from '../helpers/defaultOptionenWithOutAuth';

const initialState = {
    name: '',
    surname: '',
    email: '',
    password: ''
};

export default (state = initialState, action) => {
    switch(action.type){
        case REGISTRATION: 
       
            
        fetch('http://localhost:3300/user', {
            headers: defaultOptionenWithOutAuth.headers,
            method: 'POST',
            body: JSON.stringify({ name: state.name, surname: state.surname, email: state.email, password: state.password })
        })
        .then(response => response.json())   
            return {state}

        default: return state;

    }
}
